mapper.insert:
INSERT INTO "FILE"
(
	FILE_CODE
	, FILE_NAME
	, FILE_SN
	, FILE_MIME
	, FILE_SIZE
	, FILE_FANCYSIZE
	, FILE_NO
)
VALUES
(
	#{fileCode,jdbcType=VARCHAR}
	, #{fileName,jdbcType=VARCHAR}
	, #{fileSn,jdbcType=VARCHAR}
	, #{fileMime,jdbcType=VARCHAR}
	, #{fileSize,jdbcType=NUMERIC}
	, #{fileFancysize,jdbcType=VARCHAR}
	, (
		SELECT 
			NVL(MAX(FILE_NO),0)+1 
		FROM "FILE"
	)
)
